import React, { useState, useMemo, useCallback, useEffect } from 'react';
import Header from './components/Header';
import ButtonSearch from './components/Button/buttonSearch/buttonSearch';
import CreateUserModal from './components/Modal/CreateModal';
import UpdateUserModal from './components/Modal/UpdateModal';
import ButtonCreate from './components/Button/buttonCreate/buttonCreate';
import ButtonEdit from './components/Button/buttonEdit';
import ButtonDelete from './components/Button/buttonDelete';
import Table from './components/Table/Table';
import axios from 'axios';

const App = () => {
  const [searchResult, setSearchResult] = useState('');
  const [isCreateModalOpen, setIsCreateModalOpen] = useState(false);
  const [isUpdateModalOpen, setIsUpdateModalOpen] = useState(false);
  const [editData, setEditData] = useState(null);
  const [users, setUsers] = useState([]);

  useEffect(() => {
    const fetchUsers = async () => {
      try {
        const response = await axios.get('http://127.0.0.1:8000/api/people');
        setUsers(response.data); 
      } catch (error) {
        console.error('Error fetching users:', error);
      }
    };

    fetchUsers();
  }, []); 

  const handleSearch = (searchTerm, selectedOption) => {
    const filteredUsers = users.filter(user => {
      const matchesName = user.name.toLowerCase().includes(searchTerm.toLowerCase());
      const matchesStatus = selectedOption ? user.status === selectedOption.value : true;
      return matchesName && matchesStatus;
    });

    // Update the filtered users in the state
    setUsers(filteredUsers);
    setSearchResult(`Search term: ${searchTerm}${selectedOption ? `, Status: ${selectedOption.label}` : ''}`);
  };

  const handleOpenCreateModal = () => {
    setIsCreateModalOpen(true);
  };

  const handleCloseCreateModal = () => {
    setIsCreateModalOpen(false);
  };

  const handleOpenUpdateModal = (dataToEdit) => {
    setIsUpdateModalOpen(true);
    setEditData(dataToEdit);
  };

  const handleCloseUpdateModal = () => {
    setIsUpdateModalOpen(false);
    setEditData(null);
  };

  const handleCreateUser = async (formData) => {
    try {
      const response = await axios.post('http://127.0.0.1:8000/api/people/create', formData);
      const newUser = response.data;
      setUsers([...users, newUser]);
      handleCloseCreateModal();
    } catch (error) {
      console.error('Error creating user:', error);
    }
  };

  const handleUpdateUser = (formData) => {
    setUsers(users.map(user => (user.id === formData.id ? formData : user)));
    handleCloseUpdateModal();
  };

  const handleDeleteUser = useCallback((userId) => {
    setUsers(users.filter(user => user.id !== userId));
  }, [users]);

  const columns = useMemo(
    () => [
      {
        Header: 'ID User',
        accessor: 'id',
      },
      {
        Header: 'Name',
        accessor: 'name',
      },
      {
        Header: 'Phone',
        accessor: 'phone',
      },
      {
        Header: 'Email',
        accessor: 'email',
      },
      {
        Header: 'Status',
        accessor: 'status',
      },
      {
        Header: 'Action',
        accessor: 'action',
        Cell: ({ row }) => (
          <>
            <ButtonEdit onClick={() => handleOpenUpdateModal(row.original)} label="Edit" />
            <ButtonDelete onClick={() => handleDeleteUser(row.original.id)} label="Delete" />
          </>
        ),
      },
    ],
    [handleDeleteUser, handleOpenUpdateModal]
  );

  return (
    <div className="App">
      <Header />
      <div className="content">
        <ButtonSearch onSearch={handleSearch} />
        {searchResult}
        <ButtonCreate onClick={handleOpenCreateModal} label="Create User" />
        <Table columns={columns} data={users} />
        <CreateUserModal
          isOpen={isCreateModalOpen}
          onClose={handleCloseCreateModal}
          onCreate={handleCreateUser}
        />
        {editData && (
          <UpdateUserModal
            isOpen={isUpdateModalOpen}
            onClose={handleCloseUpdateModal}
            onUpdate={handleUpdateUser}
            initialData={editData}
          />
        )}
      </div>
    </div>
  );
};

export default App;
