const handleUpdateUser = async (formData) => {
  try {
    // Ensure formData.status is correctly set to a number (0 or 1)
    formData.status = parseInt(formData.status);

    // Send update request to API
    const response = await axios.put(`http://127.0.0.1:8000/api/people/edit/${formData.id}`, formData);

    // Check response status
    if (response.status === 200) {
      // Update users state
      setUsers(prevUsers =>
        prevUsers.map(user => (user.id === formData.id ? formData : user))
      );
      alert('User updated successfully!');
      handleCloseUpdateModal();
    } else {
      // Handle other status codes or unexpected responses
      console.error('Failed to update user:', response);
      alert('Failed to update user. Please try again later.');
    }
  } catch (error) {
    // Handle network errors or exceptions
    console.error('Error updating user:', error);
    alert('Error updating user. Please check your network connection and try again.');
  }
};
