import React, { useState } from 'react';
import Header from './components/Header';
import ButtonSearch from './components/buttonSearch/buttonSearch';
import ButtonCreate from './components/buttonCreate/buttonCreate';
import ButtonEdit from './components/buttonEdit';
import ButtonDelete from './components/buttonDelete';
import Table from './components/Table/Table'; 
import UserModal from './components/Modal/CreateModal';
import UpdateUserModal from './components/Modal/UpdateModal';

function App() {
  const [searchResult, setSearchResult] = useState('');
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [isUpdateModalOpen, setIsUpdateModalOpen] = useState(false);

  const [users, setUsers] = useState([
    { id: 1, name: 'Alice', phone: 12345667, email:'alice@gmail.com', status:'active', action: 'delete'},
    { id: 2, name: 'Bob', phone: 12233, email:'alice@gmail.com', status:'active', action: 'delete'},
  ]);

  const handleSearch = (searchTerm, selectedOption) => {
    const status = selectedOption ? selectedOption.label : '';
    setSearchResult(`Search term: ${searchTerm}${status ? `, Status: ${status}` : ''}`);
  };

  const handleOpenModal = () => {
    setIsModalOpen(true); 
  };

  const handleCloseModal = () => {
    setIsModalOpen(false); 
  };

  const handleCreateUser = (newUser) => {
    const updatedUsers = [
      ...users,
      { id: users.length + 1, ...newUser }
    ];
    setUsers(updatedUsers);
    setIsModalOpen(false); 
  };

  const handleDeleteUser = (userId) => {
    setUsers(users.filter(user => user.id !== userId));
  };

  const columns = React.useMemo(
    () => [
      { Header: 'ID User', accessor: 'id' },
      { Header: 'Name', accessor: 'name' },
      { Header: 'Phone', accessor: 'phone' },
      { Header: 'Email', accessor: 'email' },
      { Header: 'Status', accessor: 'status' },
      { Header: 'Action',
        accessor: 'action',
        Cell: ({ row }) => (
          <>
            <ButtonEdit onClick={() => handleOpenModal(row.original)} label="Edit" />
            <ButtonDelete onClick={() => handleDeleteUser(row.original.id)} label="Delete" />
          </>
        ),
      },
    ],
    []
  );

  const data = React.useMemo(
    () => users,
    [users]
  );

  return (
    <div className="App">
      <Header />
      <div className="content">
        <ButtonSearch onSearch={handleSearch} />
        {searchResult && <p>{searchResult}</p>}
        <ButtonCreate onClick={handleOpenModal} label="Create" />
        <Table columns={columns} data={data} />
        {isModalOpen && <UserModal isOpen={isModalOpen} onClose={handleCloseModal} onCreate={handleCreateUser} />}
      </div>
    </div>
  );
}

export default App;
