const handleSearch = async (searchTerm, selectedOption) => {
  try {
    let status = null;
    if (selectedOption) {
      status = selectedOption.value === 'active' ? 1 : 0;
    }

    const response = await axios.get('http://127.0.0.1:8000/api/people/search', {
      params: {
        name: `%${searchTerm}%`,
        status: status
      }
    });

    const filteredUsers = response.data;
    setUsers(filteredUsers);

    if (filteredUsers.length > 0) {
      setSearchResult(`Search term: ${searchTerm}${selectedOption ? `, Status: ${selectedOption.label}` : ''}`);
    } else {
      setSearchResult('');
    }

  } catch (error) {
    console.error('Error searching users:', error);
  }
};
