import React, { useState } from 'react';
import Header from './components/Header';
import ButtonSearch from './components/ButtonSearch';
import Table from './components/Table'; 

function App() {
  const [searchResult, setSearchResult] = useState('');

  const handleSearch = (searchTerm, selectedOption) => {
    const status = selectedOption ? selectedOption.label : '';
    setSearchResult(`Search term: ${searchTerm}${status ? `, Status: ${status}` : ''}`);
  };

  const columns = React.useMemo(
    () => [
      {
        Header: 'ID User',
        accessor: 'id',
      },
      {
        Header: 'Name',
        accessor: 'name',
      },
    ],
    []
  );

  const data = React.useMemo(
    () => [
      { name: 'Alice', age: 25 },
      { name: 'Bob', age: 30 },
      // Add more rows of data
    ],
    []
  );

  return (
    <div className="App">
      <Header />
      <div className="content">
        <ButtonSearch onSearch={handleSearch} />
        {searchResult && <p>{searchResult}</p>}
        <Table columns={columns} data={data} />
      </div>
    </div>
  );
}

export default App;
