import React, { useState, useMemo, useCallback, useEffect } from 'react';
import Header from './components/Header';
import ButtonSearch from './components/Button/buttonSearch/buttonSearch';
import CreateUserModal from './components/Modal/CreateModal';
import UpdateUserModal from './components/Modal/UpdateModal';
import ButtonCreate from './components/Button/buttonCreate/buttonCreate';
import ButtonEdit from './components/Button/buttonEdit';
import ButtonDelete from './components/Button/buttonDelete';
import Table from './components/Table/Table';
import axios from 'axios';

const App = () => {
  const [searchResult, setSearchResult] = useState('');
  const [isCreateModalOpen, setIsCreateModalOpen] = useState(false);
  const [isUpdateModalOpen, setIsUpdateModalOpen] = useState(false);
  const [editData, setEditData] = useState(null);
  const [users, setUsers] = useState([]);

  useEffect(() => {
    const fetchUsers = async () => {
      try {
        const response = await axios.get('http://127.0.0.1:8000/api/people');
        setUsers(response.data);
      } catch (error) {
        console.error('Error fetching users:', error);
      }
    };

    fetchUsers();
  }, []);

  const handleSearch = async (searchTerm, selectedOption) => {
    try {
      let status = null;
      if (selectedOption) {
        status = selectedOption.value === 'active' ? 1 : 0;
      }

      const response = await axios.get('http://127.0.0.1:8000/api/people/search', {
        params: {
          name: `%${searchTerm}%`,
          status: status
        }
      });

      const filteredUsers = response.data;
      setUsers(filteredUsers);

    } catch (error) {
      console.error('Error searching users:', error);
    }
  };  

  const handleOpenCreateModal = () => {
    setIsCreateModalOpen(true);
  };

  const handleCloseCreateModal = () => {
    setIsCreateModalOpen(false);
  };

  const handleOpenUpdateModal = (dataToEdit) => {
    setIsUpdateModalOpen(true);
    setEditData(dataToEdit);
  };

  const handleCloseUpdateModal = () => {
    setIsUpdateModalOpen(false);
    setEditData(null);
  };

  const handleCreateUser = async (formData) => {
    try {
      await axios.post('http://127.0.0.1:8000/api/people/create', formData);
      alert('User created successfully!');

      const updatedUsersResponse = await axios.get('http://127.0.0.1:8000/api/people');
      setUsers(updatedUsersResponse.data);

      handleCloseCreateModal();
    } catch (error) {
      if (error.response) {
        console.error('Server responded with:', error.response.data);
        alert('Error creating user: ' + error.response.data.message); 
      } else if (error.request) {
        console.error('Request was made but no response was received:', error.request);
        alert('Error creating user: No response received from server');
      } else {
        console.error('Error setting up the request:', error.message);
        alert('Error creating user: ' + error.message);
      }
    }
  };  

  const handleUpdateUser = async (formData) => {
    try {
      // Convert status to integer before sending to API
      const statusValue = formData.status === 'active' ? 1 : 0;
      formData.status = statusValue;

      // Send update request to API
      await axios.put(`http://127.0.0.1:8000/api/people/edit/${formData.id}`, formData);
      alert('User updated successfully!');

      setUsers(prevUsers =>
        prevUsers.map(user => (user.id === formData.id ? formData : user))
      );

      handleCloseUpdateModal();
    } catch (error) {
      console.error('Error updating user:', error);
      if (error.response) {
        console.error('Server responded with:', error.response.data);
        alert('Error updating user: ' + error.response.data.message);
      } else if (error.request) {
        console.error('Request was made but no response was received:', error.request);
        alert('Error updating user: No response received from server');
      } else {
        console.error('Error setting up the request:', error.message);
        alert('Error updating user: ' + error.message);
      }
    }
  };  

  const handleDeleteUser = useCallback(async (userId) => {
    try {
      await axios.delete(`http://127.0.0.1:8000/api/people/delete/${userId}`);
      setUsers(users.filter(user => user.id !== userId));
    } catch (error) {
      console.error('Error deleting user:', error);
    }
  }, [users]);

  const renderStatus = (row) => {
    return row.original.status === 1 ? 'Active' : 'Inactive';
  };

  const columns = useMemo(
    () => [
      {
        Header: 'ID User',
        accessor: 'id',
      },
      {
        Header: 'Name',
        accessor: 'name',
      },
      {
        Header: 'Phone',
        accessor: 'phone',
      },
      {
        Header: 'Email',
        accessor: 'email',
      },
      {
        Header: 'Status',
        accessor: 'status',
        Cell: ({ row }) => renderStatus(row),
      },
      {
        Header: 'Action',
        accessor: 'action',
        Cell: ({ row }) => (
          <>
            <ButtonEdit onClick={() => handleOpenUpdateModal(row.original)} label="Edit" />
            <ButtonDelete onClick={() => handleDeleteUser(row.original.id)} label="Delete" />
          </>
        ),
      },
    ],
    [handleDeleteUser, handleOpenUpdateModal]
  );

  return (
    <div className="App">
      <Header />
      <div className="content">
        <ButtonSearch onSearch={handleSearch} />
        {searchResult}
        <ButtonCreate onClick={handleOpenCreateModal} label="Create User" />
        <Table columns={columns} data={users} />
        <CreateUserModal
          isOpen={isCreateModalOpen}
          onClose={handleCloseCreateModal}
          onCreate={handleCreateUser}
        />
        {editData && (
          <UpdateUserModal
            isOpen={isUpdateModalOpen}
            onClose={handleCloseUpdateModal}
            onUpdate={handleUpdateUser}
            initialData={editData}
          />
        )}
      </div>
    </div>
  );
};

export default App;
