import React, { useState } from 'react';
import CreateUserModal from './components/Modal/CreateModal';
import UpdateUserModal from './components/Modal/UpdateModal';
import ButtonCreate from './components/buttonCreate/buttonCreate';
import ButtonEdit from './components/buttonEdit';
import ButtonDelete from './components/buttonDelete';
import Table from './components/Table/Table';

const App = () => {
  const [isCreateModalOpen, setIsCreateModalOpen] = useState(false);
  const [isUpdateModalOpen, setIsUpdateModalOpen] = useState(false);
  const [editData, setEditData] = useState(null); 
  const [users, setUsers] = useState([
    { id: 1, name: 'Alice', phone: '12345667', email: 'alice@gmail.com', status: 'active' },
    { id: 2, name: 'Bob', phone: '12233', email: 'bob@gmail.com', status: 'active' },
  ]);

  const handleOpenCreateModal = () => {
    setIsCreateModalOpen(true);
  };

  const handleCloseCreateModal = () => {
    setIsCreateModalOpen(false);
  };

  const handleOpenUpdateModal = (dataToEdit) => {
    setIsUpdateModalOpen(true);
    setEditData(dataToEdit);
  };

  const handleCloseUpdateModal = () => {
    setIsUpdateModalOpen(false);
    setEditData(null);
  };

  const handleCreateUser = (formData) => {
    setUsers([...users, { ...formData, id: users.length + 1 }]);
    handleCloseCreateModal();
  };

  const handleUpdateUser = (formData) => {
    setUsers(users.map(user => (user.id === formData.id ? formData : user)));
    handleCloseUpdateModal();
  };

  const handleDeleteUser = (userId) => {
    setUsers(users.filter(user => user.id !== userId));
  };

  const columns = React.useMemo(
    () => [
      {
        Header: 'ID User',
        accessor: 'id',
      },
      {
        Header: 'Name',
        accessor: 'name',
      },
      {
        Header: 'Phone',
        accessor: 'phone',
      },
      {
        Header: 'Email',
        accessor: 'email',
      },
      {
        Header: 'Status',
        accessor: 'status',
      },
      {
        Header: 'Action',
        accessor: 'action',
        Cell: ({ row }) => (
          <>
            <ButtonEdit onClick={() => handleOpenUpdateModal(row.original)} label="Edit" />
            <ButtonDelete onClick={() => handleDeleteUser(row.original.id)} label="Delete" />
          </>
        ),
      },
    ],
    []
  );

  return (
    <div className="App">
      <ButtonCreate onClick={handleOpenCreateModal} label="Create User" />
      <Table columns={columns} data={users} />

      <CreateUserModal
        isOpen={isCreateModalOpen}
        onClose={handleCloseCreateModal}
        onCreate={handleCreateUser}
      />

      {editData && (
        <UpdateUserModal
          isOpen={isUpdateModalOpen}
          onClose={handleCloseUpdateModal}
          onUpdate={handleUpdateUser}
          initialData={editData}
        />
      )}
    </div>
  );
};

export default App;
